import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";


const Card = ({ name, username, id }) => {
  const [favsSelect, setFavsSelect] = useState({})
  
  const url = "https://jsonplaceholder.typicode.com/users/" + id
  useEffect(() =>{
    fetch(url)
    .then(response => response.json())
    .then(data => setFavsSelect(data))
  }, [])
  
  const addFav = ()=>{
    // Aqui iria la logica para agregar la Card en el localStorage
    let favs = localStorage.getItem('favs')
    if(favs){
      let parsedFavs = JSON.parse(favs)
      const idFavs = parsedFavs.find((o)=>{ o.id == id})
      console.log(idFavs)
      favs = [...parsedFavs, favsSelect]
      console.log(parsedFavs)
      console.log("parsedFavs")
    } else{
      favs = [favsSelect]
      console.log(favs )
      console.log("pruena no")
    }
    localStorage.setItem('favs', JSON.stringify(favs))
    

    //localStorage.setItem(id,name)
    //const keys = Object.keys(localStorage);
    //const values = keys.map(key => console.log(key));
  }

  return (
    <div className="card"> 
        {/* En cada card deberan mostrar en name - username y el id */}
        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}
        <Link className="card" key={id} to={'/dentist/'+ id}>
          <h3>{name}</h3>
          <img src="./images/doctor.jpg" alt='Doctor' width={90} />
          <h3>{username}</h3>          
        </Link>
    
        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}
        <button onClick={addFav} className="favButton"> ‚≠ê </button>
    </div>
  );
};

export default Card;
