import React from 'react'
import { useParams } from 'react-router-dom'
import { useContextGlobal } from '../Components/utils/global.context'


//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Detail = () => {
 
  // Consumiendo el parametro dinamico de la URL deberan hacer un fetch a un user en especifico

  const {dentists} = useContextGlobal()
  const params = useParams()
  
  const dentistSelect = dentists.find((dentist) => params.id == dentist.id)
  console.log(dentistSelect)

  return (
    <>
      <h1>Detail Dentist  </h1>
      <h3>Nombre: {dentistSelect.name}</h3>
      <h3>Email: {dentistSelect.email}</h3>
      <h3>Telefono: {dentistSelect.phone}</h3>
      <h3>Website: {dentistSelect.website}</h3>
      {/* aqui deberan renderizar la informacion en detalle de un user en especifico */}
      {/* Deberan mostrar el name - email - phone - website por cada user en especifico */}
    </>
  )
}

export default Detail
